const btn=document.querySelector(".fork-app-dropdown"),menu=document.querySelector(".fork-app-menu"),li=document.querySelectorAll(".fork-app-menu-div li"),body=document.querySelector("body");btn.addEventListener("click",()=>{btn.classList.toggle("closed"),menu.classList.toggle("open"),event.stopPropagation()}),li.forEach(e=>{e.addEventListener("click",()=>{li.forEach(e=>{e.classList.remove("fork-app-selected")}),e.classList.add("fork-app-selected")})}),document.addEventListener("click",function(e){menu.contains(e.target)||(menu.classList.remove("open"),btn.classList.add("closed"))});